class Solution(object):
    def exist(self, board, word):
        self.res = False
        for i, row in enumerate(board):
            for j, v in enumerate(row):
                if v == word[0]:
                    visited = set()
                    self.dfs(board, word, i, j, 0, visited)
        return self.res

    def dfs(self, board, word, i, j, cnt, visited):
        visited.add((i, j))
        direction = [(1, 0), (-1, 0), (0, 1), (0, -1)]
        cnt += 1
        if cnt == len(word):
            self.res = True
            return
        for direct in direction:
            (y, x) = (direct[0]+i, direct[1]+j)
            if 0 <= x < len(board[0]) and 0 <= y < len(board) and board[y][x] == word[cnt] and (y, x) not in visited:
                visited_copy = visited.copy()
                visited_copy.add((y, x))
                self.dfs(board, word, y, x, cnt, visited_copy)
                if self.res == True:
                    return True


